var mods = ["client", "session"];

for (var i = 0, l = mods.length; i < l; ++i) {
    (function (mod) {
        Object.defineProperty(exports, mod, {
            get: function () {
                if (!this["_" + mod]) {
                    this["_" + mod] = require("./buster-client/" + mod);
                }

                return this["_" + mod];
            }
        });
    }(mods[i]));
}
